INSERT INTO `wp_posts` VALUES (118,1,'2024-03-22 07:21:05','2024-03-22 07:21:05','<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"20%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:20%\"><!-- wp:image {\"id\":44,\"width\":\"93px\",\"height\":\"auto\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full is-resized\"><img src=\"http://planty.local/wp-content/uploads/2024/03/tige-g.png\" alt=\"\" class=\"wp-image-44\" style=\"width:93px;height:auto\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"60%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:60%\"><!-- wp:image {\"align\":\"center\",\"id\":45,\"width\":\"304px\",\"height\":\"auto\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image aligncenter size-full is-resized\"><img src=\"http://planty.local/wp-content/uploads/2024/03/cannette.png\" alt=\"\" class=\"wp-image-45\" style=\"width:304px;height:auto\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"20%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:20%\"><!-- wp:image {\"id\":46,\"width\":\"98px\",\"height\":\"auto\",\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full is-resized\"><img src=\"http://planty.local/wp-content/uploads/2024/03/tige-d.png\" alt=\"\" class=\"wp-image-46\" style=\"width:98px;height:auto\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:heading {\"textAlign\":\"center\",\"level\":5,\"style\":{\"elements\":{\"link\":{\"color\":{\"text\":\"#3b8e46\"}}},\"color\":{\"text\":\"#3b8e46\"},\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"600\"}}} -->\n<h5 class=\"wp-block-heading has-text-align-center has-text-color has-link-color\" style=\"color:#3b8e46;font-style:normal;font-weight:600\">L\'énergie des plantes</h5>\n